package com.tpi.servicio_flota.modelo;

import jakarta.persistence.*;
import jakarta.validation.constraints.NotBlank;
import jakarta.validation.constraints.Positive;
import lombok.*;

/**
 * Entidad que representa un camión de la flota.
 * Según DER: patente, nombre_transportista, telefono_transportista, 
 * capacidad_peso, capacidad_volumen, consumo_combustible_km, costo_km, disponible
 */
@Entity
@Table(name = "camiones")
@Getter
@Setter
@NoArgsConstructor
@AllArgsConstructor
@Builder
public class Camion {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    @NotBlank(message = "La patente es obligatoria")
    @Column(unique = true, nullable = false)
    private String patente;

    @NotBlank(message = "El nombre del transportista es obligatorio")
    @Column(name = "nombre_transportista")
    private String nombreTransportista;

    @Column(name = "telefono_transportista")
    private String telefonoTransportista;

    @Positive(message = "La capacidad de peso debe ser mayor a 0")
    @Column(name = "capacidad_peso")
    private Double capacidadPeso;

    @Positive(message = "La capacidad de volumen debe ser mayor a 0")
    @Column(name = "capacidad_volumen")
    private Double capacidadVolumen;

    @Positive(message = "El consumo de combustible debe ser mayor a 0")
    @Column(name = "consumo_combustible_km")
    private Double consumoCombustibleKm;

    @Positive(message = "El costo por km debe ser mayor a 0")
    @Column(name = "costo_km")
    private Double costoKm;

    @Column(nullable = false)
    private Boolean disponible = true;
}

package com.tpi.servicio_logistica.controlador;

import com.tpi.servicio_logistica.modelo.Configuracion;
import com.tpi.servicio_logistica.servicio.ConfiguracionServicio;
import jakarta.validation.Valid;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import java.util.List;

/**
 * Controlador REST para gestionar configuraciones.
 */
@RestController
@RequestMapping("/api/configuraciones")
public class ConfiguracionControlador {

    private final ConfiguracionServicio servicio;

    public ConfiguracionControlador(ConfiguracionServicio servicio) {
        this.servicio = servicio;
    }

    @GetMapping
    public List<Configuracion> listarTodas() {
        return servicio.listar();
    }

    @GetMapping("/{id}")
    public ResponseEntity<Configuracion> buscarPorId(@PathVariable Long id) {
        return servicio.buscarPorId(id)
                .map(ResponseEntity::ok)
                .orElse(ResponseEntity.notFound().build());
    }

    @GetMapping("/clave/{clave}")
    public ResponseEntity<Configuracion> buscarPorClave(@PathVariable String clave) {
        return servicio.buscarPorClave(clave)
                .map(ResponseEntity::ok)
                .orElse(ResponseEntity.notFound().build());
    }

    @PostMapping
    public ResponseEntity<Configuracion> crear(@Valid @RequestBody Configuracion configuracion) {
        Configuracion nueva = servicio.guardar(configuracion);
        return ResponseEntity.ok(nueva);
    }

    @PutMapping("/{id}")
    public ResponseEntity<Configuracion> actualizar(@PathVariable Long id,
                                                    @Valid @RequestBody Configuracion datos) {
        return ResponseEntity.ok(servicio.actualizar(id, datos));
    }

    @DeleteMapping("/{id}")
    public ResponseEntity<Void> eliminar(@PathVariable Long id) {
        servicio.eliminar(id);
        return ResponseEntity.noContent().build();
    }
}

